/**
 * The token interface represents the structure of the token returned by
 * the Camunda 8 Identity endpoint. It contains the access token, scope, expiration time,
 * token type, and audience.
 */
export interface Token {
    access_token: string;
    scope: string;
    expires_in: number;
    token_type: string;
    expiry: number;
    audience: string;
}
export interface TokenError {
    error: string;
    error_description: string;
}
export { BasicAuthProvider } from '@camunda8/sdk/dist/oauth/lib/BasicAuthProvider';
export { BearerAuthProvider } from '@camunda8/sdk/dist/oauth/lib/BearerAuthProvider';
export { CookieAuthProvider } from '@camunda8/sdk/dist/oauth/lib/CookieAuthProvider';
export { IHeadersProvider } from '@camunda8/sdk/dist/oauth/lib/IHeadersProvider';
export { NullAuthProvider } from '@camunda8/sdk/dist/oauth/lib/NullAuthProvider';
export { OAuthProvider } from '@camunda8/sdk/dist/oauth/lib/OAuthProvider';
